$(document).ready(function(){$(".modal-sizes").magnificPopup({type:"inline",preloader:!1,modal:!0}),$(".modal-dismiss").click(function(){$.magnificPopup.close()}),$("#add-withdraw-form").submit(function(e){e.preventDefault(),$.ajax({url:$(this).attr("action"),type:"POST",data:{amount:$("#add-amount").val()},headers:{"X-CSRF-TOKEN":$("#csrf-token").attr("content")},success:function(){location.reload()},error:function(e){new PNotify({title:"Error",text:e.responseJSON.message,type:"error",addclass:"notification-error",icon:"fas fa-times"})}})}),$(".cancel-withdraw").click(function(e){e.preventDefault(),$.ajax({url:window.baseUrl+"/admin/ecommerce/vendor-withdraw/cancel/"+$(this).data("id"),type:"PUT",headers:{"X-CSRF-TOKEN":$("#csrf-token").attr("content")},success:function(){location.reload()},error:function(e){new PNotify({title:"Error",text:e.responseJSON.message,type:"error",addclass:"notification-error",icon:"fas fa-times"})}})})});